(19, 8, 3, '企业内刊', '企业内刊', '/output/pic/a5e37b84b6d7a0f1224.jpg', '下载专区_企业内刊', '下载专区_企业内刊', '下载专区_企业内刊', '下载专区_企业内刊', '', 'unitorch', 'down/mag.php', 0, 0, '2015-10-06 19:58:34', '2015-10-07 22:55:39', 0, 1, 0, NULL, NULL, NULL, 0, ''),
(20, 8, 3, '资料文件', '资料文件', '/output/pic/a5e37b84b6d7a0f1224.jpg', '下载专区_资料文件', '下载专区_资料文件', '下载专区_资料文件', '下载专区_资料文件', '', 'unitorch', 'down/file.php', 0, 0, '2015-10-06 19:59:00', '2015-10-07 22:43:28', 0, 1, 0, NULL, NULL, NULL, 0, ''),
(21, 0, 1, '人才招聘', '人才招聘', '/output/pic/a5e37b84b6d7a0f1224.jpg', '人才招聘', '人才招聘', '人才招聘', '人才招聘', '&lt;div class=&quot;policy&quot;&gt;\n          &lt;p&gt;&lt;strong&gt;科技项目经理&lt;/strong&gt;&lt;/p&gt;\n          &lt;p&gt;一、岗位职责：&lt;/p&gt;\n          &lt;p&gt;1、及时掌握国家、政府各类项目申报动态，熟悉科技项目申报各项流程；&lt;/p&gt;\n          &lt;p&gt;2、根据公司全年项目申报计划、对申报项目进行可行性分析、推进意见及前期准备；&lt;/p&gt;\n          &lt;p&gt;3、负责公司及其孵化企业项目申报的咨询服务工作，主导编写并及时上报项目申报材料；&lt;/p&gt;\n          &lt;p&gt;4、指导并带领团队人员做好项目申报、结题验收的工作；&lt;/p&gt;\n          &lt;p&gt;5、针对入驻企业开展相关项目资源申报的培训工作；&lt;/p&gt;\n          &lt;p&gt;6、与项目申报相关部门、在孵企业保持良好关系；&lt;/p&gt;\n          &lt;p&gt;7、负责为部分企业提供高新技术企业、技术成果、技术进步奖等认定及相关荣誉的服务工作。&lt;/p&gt;\n          &lt;p&gt;二、任职要求：&lt;/p&gt;\n          &lt;p&gt;1、本科以上学历，理工科专业、财务专业优先，有孵化器从业经验优先考虑；接受过技术管理、项目管理等方面的培训；&lt;/p&gt;\n          &lt;p&gt;2、至少4年以上相关工作经验，2年以上项目管理、科技服务咨询辅导、商业企划及商务谈判工作经验；&lt;/p&gt;\n          &lt;p&gt;3、熟悉科技项目申报流程，熟悉各类别项目申报软件的使用；&lt;/p&gt;\n          &lt;p&gt;4、具备对项目进行可行性分析的能力和一定财务分析能力，较好的组织协调能力，优秀的公关能力和良好的文笔。&lt;/p&gt;\n          &lt;p&gt;&lt;br&gt;\n          &lt;/p&gt;\n          &lt;p&gt;&lt;strong&gt;科技服务专员&lt;/strong&gt;&lt;/p&gt;\n          &lt;p&gt;一、岗位职责：&lt;/p&gt;\n          &lt;p&gt;1、查找、收集、整合各政府网站关于科技项目申报、批复文件及优惠政策等信息；&lt;/p&gt;\n          &lt;p&gt;2、协助部门经理完成项目的立项、申报、审批、验收等各环节工作；&lt;/p&gt;\n          &lt;p&gt;3、查找、搜集各行业的项目申报信息及相关部门的沟通与协调；&lt;/p&gt;\n          &lt;p&gt;4、积极走访企业，有针对性为企业提供项目申报服务；&lt;/p&gt;\n          &lt;p&gt;5、建立健全企业服务档案，做好孵化器相关统计工作。&amp;nbsp;&lt;/p&gt;\n          &lt;p&gt;二、任职要求：&lt;/p&gt;\n          &lt;p&gt;1、大专以上学历，理工科专业、财务专业优先，有孵化器从业经验优先考虑；接受过技术管理、项目管理等方面的培训；&lt;/p&gt;\n          &lt;p&gt;2、至少2年以上相关工作经验，有孵化器从业经验优先考虑；接受过技术管理、项目管理等方面的培训；&lt;/p&gt;\n          &lt;p&gt;3、熟悉科技项目申报流程，具备对项目进行可行性分析的能力；熟悉各类别项目申报软件的使用；&lt;/p&gt;\n          &lt;p&gt;4、具备良好的公文写作能力及公关能力；良好的组织协调及客户沟通能力。&lt;/p&gt;\n          &lt;p&gt;&lt;br&gt;\n          &lt;/p&gt;\n          &lt;p&gt;有意者请先投递简历至邮箱1074128330@qq.com&lt;/p&gt;\n        &lt;/div&gt;', 'unitorch', 'join.php', 0, 0, '2015-10-06 19:59:31', '2015-10-08 20:12:45', 0, 1, 0, NULL, NULL, NULL, 0, ''),
